{%  comment %}QUICK LINKS{% endcomment %}
{%- if product.type != "Accessory" %}
  <div class="quicklinks">
  {% assign onlySku = product.selected_or_first_available_variant.sku | split: ' ' %}
  {% for part in onlySku %}
    {% if forloop.first %}
      {% assign onlySku = part | strip %}
    {% endif %}
  {% endfor %}
  {%- if product.tags contains "High Bay" and product.tags contains "Fixture" -%}
    {%- capture link-layout -%}
      <a class="product-form__add-button link linewrapper" href="javascript:window.open('/pages/free-high-bay-lighting-layout','_blank')">Get&nbsp;a&nbsp;Layout</a>
    {%- endcapture -%}
  {%- elsif product.tags contains "Low Bay" and product.tags contains "Fixture" -%}
    {%- capture link-layout -%}
      <a class="product-form__add-button link linewrapper" href="javascript:window.open('/pages/free-high-bay-lighting-layout','_blank')">Get&nbsp;a&nbsp;Layout</a>
    {%- endcapture -%}
  {%- endif -%} 
  {%- capture link-pricematch -%}<a href="/pages/price-match?sku={{ onlySku }}" class="product-form__add-button link linewrapper" target="_blank">Price&nbsp;Match</a>{%- endcapture %}
  {%- if product.type == "LED" or product.type == "Grow Light" -%}
    {%- capture link-calculator -%}<a class="product-form__add-button link linewrapper" href="javascript:void(0);" onClick="doLinkToCalc()">Check&nbsp;Energy&nbsp;Savings</a>{%- endcapture -%}
    <script>
    {% for variant in product.variants %}
        {% if product.selected_or_first_available_variant.id == variant.id %}
            {% assign vwatts = variant.metafields.data.variant_dlc_wattage %}
            {% assign vsku = variant.sku %}
            {% assign prodprice = variant.price %}
        {% endif %}
    {% endfor %}
    var vwatts = "{{ vwatts }}";
    var pwatts = "{{ product.metafields.data.spec_wattage }}";
    var prodprice = "{{ prodprice }}";
    var vsku = "{{ vsku }}";
    /* find pack type - (Pack of ##) or (## Pack) */
    if(vsku.includes("Pack)")) {
        var vtpacka = vsku.split(' (');
        var vtpack = vtpacka[1].split(' ');
        var vpack = parseInt(vtpack[0]);
    } else {
      var vtpack = vsku.split(' (Pack of ');
      var vpack = parseInt(vtpack[1]);
    }
    var rwatts = "{{ product.metafields.data.spec_replaces_wattage }}";
    if(isNaN(vpack)) { vpack = 1; }
    if(vwatts == "") { 
        var tmpwatts = pwatts; 
    } else {
        var tmpwatts = vwatts;
    }
    var tmpwatts2 = tmpwatts.split("W");
    if(tmpwatts2.length == 1) {
        vwatts = tmpwatts2;
    } else {
        vwatts = tmpwatts2[0];
    }
    if(rwatts != "") { 
        var rwatts1 = rwatts.split("W");
        if(rwatts1.length >1 && rwatts1[1] != '') {
            rwatts = rwatts1[1].replace( /[^0-9]/g, '' );
        } else {
            rwatts = rwatts1[0].replace( /[^0-9]/g, '' );
        }
    } else {
        rwatts=0;
    }
    function doLinkToCalc() {
        var urllink = "/pages/energy-savings-calculator?w="+vwatts+"&c="+prodprice/100/vpack+"&q="+vpack*$('.product-form__quantity .quantity-selector__input').val()+"&r="+rwatts;
        window.open(urllink, '_blank');
        return false;
    }
    </script>
{% endif %}

{%  assign looking = "<em>Looking for more? </em>" %}
{%- assign do-or = " or&nbsp;" -%}
{%- assign do-comma = ",&nbsp;" -%}

{% assign linkcounter = 0 %}
{% if link-accessories.first %}
  {% assign linkcounter = linkcounter | plus: 1 %}
{% endif %}
{% if link-layout.first %}
  {% assign linkcounter = linkcounter | plus: 1 %}
{% endif %}
{% if link-pricematch.first %}
  {% assign linkcounter = linkcounter | plus: 1 %}
{% endif %}
{% case linkcounter %}
  {% when 0 %}
    {{ link-pricematch }}{% if link-calculator %}{{ do-or }}{{ link-calculator }}{% endif %}
  {% when 1 %}
    {{ looking }}{{ link-accessories }}{{ link-layout }}{{ link-pricematch }}{% if link-calculator %}{{ do-or }}{{ link-calculator }}{% endif %}
  {% when 2 %}
    {% if link-accessories.first %}
      {{ looking }}{{ link-accessories }}{{ do-comma }}{{ link-pricematch }}{% if link-calculator %}{{ do-or }}{{ link-calculator }}{% endif %}
    {% else %}
      {{ looking }}{{ link-layout }}{{ do-comma }}{{ link-pricematch }}{% if link-calculator %}{{ do-or }}{{ link-calculator }}{% endif %}
    {% endif %}
  {%  when 3 %}
      {{ looking }}{{ link-accessories }}{{ do-comma }}{{ link-layout }}{{ do-comma }}{{ link-pricematch }}{% if link-calculator %}{{ do-or }}{{ link-calculator }}{% endif %}
{% endcase %}

 <style>
      .quicklinks.mobile { display: block; }
      .quicklinks.desktop { display: none; }
      @media screen and (min-width: 1000px) {
        .quicklinks.mobile { display: none; }
        .quicklinks.desktop { display: block; }
      }
      .quicklinks {
        margin: 10px 0;
        font-size: 15px;
      }
      .popover__content .quicklinks {
        margin-bottom: 120px;
      }
      a.product-form__add-button.link {
          color: #23b26b;
          font-weight: bold;
      }
  </style>
            </div>{% endif %}
{%  comment %}END QUICK LINKS{% endcomment %}

{%- capture productswatches -%}{% assign families = shop.metafields.global.app_product_families.value['list'] %}
{% for family in families %}
    {% assign names = family[3] | split: ", " %}
    {% for name in names %}
        {% if name == product.handle %}
            {% comment %} found a match - use data to build buttons {% endcomment %}
            {% assign spec = family[1] | split: "/" %}
            {% assign seperator = "" %}
            {% for prodhandle in names %}
                {% if prodhandle != product.handle %}
                    {% if all_products[prodhandle].metafields[spec[0]][spec[1]].value != null and all_products[prodhandle].url != null %}
                        {% assign lab = all_products[prodhandle].metafields[spec[0]][spec[1]].value | append: "||" | append: all_products[prodhandle].url %}
                        {% assign buttons = buttons | append: lab | append: "|||" %} 
                    {% endif %}
                {% endif %}
            {% endfor %}

            {% assign buttons = buttons | split: "|||"%}
            <h3 class="short-htag">{{ family[2] }}&nbsp;&nbsp;</h3>
            <span class="product-families">
            {% assign comma = "" %}
                    {% for button in buttons %}
                        {% assign buttondata = button | split: "||" %}
                        {{ comma }}<a href="{{ buttondata[1] }}" class="link">{{ buttondata[0] | strip }}</a>
                        {% assign comma = "&nbsp;|&nbsp;&nbsp;" %}
                    {% endfor %}
            </span>
            {% break %}
        {% endif %}
    {% endfor %}
{% endfor %}            
{%- endcapture -%}
{% if productswatches != blank or productswatches != '' %}
    {{ productswatches }}
    <style>
    .short-htag {
        display: inline;
    }
    .product-families a {
        color: blue;
        font-weight: bold;
    }
    </style>
{% endif %}

{% comment %}START Upsell Products - read from global data created in ELED Apps Upsells{% endcomment %}
    {%- capture upsellProducts | strip_newlines -%}
            {% assign upsell_got = false %}
            {% for upsell_products_set in shop.metafields.global.app_product_upsells.value['list'] %}
                {% if upsell_products_set[0] == product.handle %}
                    {% assign upsell_products = upsell_products_set[1] | split: "," %}
                    {% for up in upsell_products %}
                        {% if all_products[up].available == true %}
                            {% assign upsell_got = true %}
                            {% break %}
                        {% endif %}
                    {% endfor %}
                {% endif %}
            {% endfor %}
            {% if upsell_got == true %}
                <div class="product-content__featured-products top-half">
                <div class="sticky">
                    <p class="product-content__featured-products-title heading heading--small">Customers Also Purchase</p>
                    <div class="scroller">
                        <div class="scroller__inner">
                            <div class="product-content__featured-products-list">
                                {%- if upsell_products[0] != blank -%}
                                    {%- capture item_cap | strip_newlines -%}
                                        {%- render 'product-item', product: all_products[upsell_products[0]], no_quickview: true, reduced_content: true, minimum_content: true, reduced_font_size: true, sizes_attribute: '(max-width: 740px) 105px, 215px' | strip_newlines -%}
                                    {%- endcapture -%}
                                    {% if item_cap contains "no-image-" or item_cap contains "Sold Out" %}
                                    {% else %}
                                        {{- item_cap -}}
                                    {% endif %}
                                {%- endif -%}

                                {%- if upsell_products[1] != blank -%}
                                    {%- capture item_cap | strip_newlines -%}
                                        {%- render 'product-item', product: all_products[upsell_products[1]], no_quickview: true, reduced_content: true, minimum_content: true, reduced_font_size: true, sizes_attribute: '(max-width: 740px) 105px, 215px' | strip_newlines -%}
                                    {%- endcapture -%}
                                    {% if item_cap contains "no-image-" or item_cap contains "Sold Out" %}
                                    {% else %}
                                        {{- item_cap -}}
                                    {% endif %}
                                {%- endif -%}
                            </div>
                        </div>
                    </div>
                </div>
                </div>
            {%- endif -%}
    {%- endcapture -%}
    {{- upsellProducts | strip_newlines -}}
    {% comment %}END Upsell Products{% endcomment %}
<style>
.product-content__featured-products.top-half .product-content__featured-products-list {
grid-template-columns: 1fr 1fr;
}
@media screen and (max-width: 450px) {
